
public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double score = 4.5;
		
		if (score>=4.5) {
			System.out.println("당신 천재지");
		}else if (score >=3.5) {
			System.out.println("당신 잘했어");
		}else {
			System.out.println("당신 ^^");
		}
	}

}
