
public class TypeInference {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		var j = 10.0;
		var str = "Hello";
		
		System.out.println(i);
		System.out.println(j);
		System.out.println(str);
		
		str = "test"; // var 변하는 변수

		System.out.println(str);
		
		}
}
